SELECT diagnosis.uuid AS uuid, diagnosis.created_at AS created_at, diagnosis.modified_at AS modified_at, diagnosis.visible AS visible, diagnosis.external_id AS external_id, diagnosis.source_text_diagnosis AS source_text_diagnosis, diagnosis.diagnosis_category AS diagnosis_category, diagnosis.source_text_tumor_location AS source_text_tumor_location, diagnosis.age_at_event_days AS age_at_event_days, diagnosis.mondo_id_diagnosis AS mondo_id_diagnosis, diagnosis.icd_id_diagnosis AS icd_id_diagnosis, diagnosis.uberon_id_tumor_location AS uberon_id_tumor_location, diagnosis.ncit_id_diagnosis AS ncit_id_diagnosis, diagnosis.spatial_descriptor AS spatial_descriptor, diagnosis.participant_id AS participant_id, diagnosis.kf_id AS kf_id 
FROM participant JOIN diagnosis ON participant.kf_id = diagnosis.participant_id 
WHERE participant.study_id = 'REPLACE_STUDY_ID'