SELECT genomic_file.uuid, genomic_file.latest_did, genomic_file.created_at, genomic_file.modified_at, genomic_file.visible, genomic_file.external_id, genomic_file.data_type, genomic_file.file_format, genomic_file.is_harmonized, genomic_file.reference_genome, genomic_file.controlled_access, genomic_file.availability, genomic_file.paired_end, genomic_file.kf_id 
FROM genomic_file JOIN biospecimen_genomic_file ON genomic_file.kf_id = biospecimen_genomic_file.genomic_file_id JOIN biospecimen ON biospecimen.kf_id = biospecimen_genomic_file.biospecimen_id JOIN participant ON participant.kf_id = biospecimen.participant_id 
WHERE participant.study_id = 'REPLACE_STUDY_ID' GROUP BY genomic_file.kf_id