SELECT phenotype.uuid AS uuid, phenotype.created_at AS created_at, phenotype.modified_at AS modified_at, phenotype.visible AS visible, phenotype.external_id AS external_id, phenotype.source_text_phenotype AS source_text_phenotype, phenotype.hpo_id_phenotype AS hpo_id_phenotype, phenotype.snomed_id_phenotype AS snomed_id_phenotype, phenotype.observed AS observed, phenotype.age_at_event_days AS age_at_event_days, phenotype.participant_id AS participant_id, phenotype.kf_id AS kf_id 
FROM participant JOIN phenotype ON participant.kf_id = phenotype.participant_id 
WHERE participant.study_id = 'REPLACE_STUDY_ID'