SELECT sequencing_experiment.uuid, sequencing_experiment.created_at, sequencing_experiment.modified_at, sequencing_experiment.visible, sequencing_experiment.external_id, sequencing_experiment.experiment_date, sequencing_experiment.experiment_strategy, sequencing_experiment.library_name, sequencing_experiment.library_strand, sequencing_experiment.library_selection, sequencing_experiment.library_prep, sequencing_experiment.is_paired_end, sequencing_experiment.platform, sequencing_experiment.instrument_model, sequencing_experiment.max_insert_size, sequencing_experiment.mean_insert_size, sequencing_experiment.mean_depth, sequencing_experiment.total_reads, sequencing_experiment.mean_read_length, sequencing_experiment.sequencing_center_id, sequencing_experiment.kf_id 
FROM sequencing_experiment JOIN sequencing_experiment_genomic_file ON sequencing_experiment.kf_id = sequencing_experiment_genomic_file.sequencing_experiment_id JOIN genomic_file ON genomic_file.kf_id = sequencing_experiment_genomic_file.genomic_file_id JOIN biospecimen_genomic_file ON genomic_file.kf_id = biospecimen_genomic_file.genomic_file_id JOIN biospecimen ON biospecimen.kf_id = biospecimen_genomic_file.biospecimen_id JOIN participant ON participant.kf_id = biospecimen.participant_id 
WHERE participant.study_id = 'REPLACE_STUDY_ID' GROUP BY sequencing_experiment.kf_id