SELECT sequencing_experiment.uuid AS uuid, sequencing_experiment.created_at AS created_at, sequencing_experiment.modified_at AS modified_at, sequencing_experiment.visible AS visible, sequencing_experiment.external_id AS external_id, sequencing_experiment.experiment_date AS experiment_date, sequencing_experiment.experiment_strategy AS experiment_strategy, sequencing_experiment.library_name AS library_name, sequencing_experiment.library_strand AS library_strand, sequencing_experiment.library_selection AS library_selection, sequencing_experiment.library_prep AS library_prep, sequencing_experiment.is_paired_end AS is_paired_end, sequencing_experiment.platform AS platform, sequencing_experiment.instrument_model AS instrument_model, sequencing_experiment.max_insert_size AS max_insert_size, sequencing_experiment.mean_insert_size AS mean_insert_size, sequencing_experiment.mean_depth AS mean_depth, sequencing_experiment.total_reads AS total_reads, sequencing_experiment.mean_read_length AS mean_read_length, sequencing_experiment.sequencing_center_id AS sequencing_center_id, sequencing_experiment.kf_id AS kf_id 
FROM sequencing_experiment JOIN sequencing_experiment_genomic_file ON sequencing_experiment.kf_id = sequencing_experiment_genomic_file.sequencing_experiment_id JOIN genomic_file ON genomic_file.kf_id = sequencing_experiment_genomic_file.genomic_file_id JOIN biospecimen_genomic_file ON genomic_file.kf_id = biospecimen_genomic_file.genomic_file_id JOIN biospecimen ON biospecimen.kf_id = biospecimen_genomic_file.biospecimen_id JOIN participant ON participant.kf_id = biospecimen.participant_id 
WHERE participant.study_id = 'REPLACE_STUDY_ID' GROUP BY sequencing_experiment.kf_id