SELECT sequencing_experiment_genomic_file.uuid AS uuid, sequencing_experiment_genomic_file.created_at AS created_at, sequencing_experiment_genomic_file.modified_at AS modified_at, sequencing_experiment_genomic_file.visible AS visible, sequencing_experiment_genomic_file.sequencing_experiment_id AS sequencing_experiment__1, sequencing_experiment_genomic_file.genomic_file_id AS genomic_file_id, sequencing_experiment_genomic_file.external_id AS external_id, sequencing_experiment_genomic_file.kf_id AS kf_id 
FROM sequencing_experiment_genomic_file JOIN genomic_file ON genomic_file.kf_id = sequencing_experiment_genomic_file.genomic_file_id JOIN biospecimen_genomic_file ON genomic_file.kf_id = biospecimen_genomic_file.genomic_file_id JOIN biospecimen ON biospecimen.kf_id = biospecimen_genomic_file.biospecimen_id JOIN participant ON participant.kf_id = biospecimen.participant_id 
WHERE participant.study_id = 'REPLACE_STUDY_ID' GROUP BY sequencing_experiment_genomic_file.kf_id